    Roles<span class="token punctuation">[</span>Roles<span class="token punctuation">[</span><span class="token string">"Admin"</span><span class="token punctuation">]</span> <span class="token operator">=</span> <span class="token number">1</span><span class="token punctuation">]</span> <span class="token operator">=</span> <span class="token string">"Admin"</span><span class="token punctuation">;</span>
